package com.example.greyscaler.phonerecord;import android.content.Context;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.DefaultItemAnimator;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.CheckBox;import android.widget.TextView;import java.util.ArrayList;import java.util.List;public class MainActivity extends AppCompatActivity {    private List<Contact> contactList = new ArrayList<>();    private RecyclerView recyclerView;    private CallAdapter mAdapter;    DatabaseHandler db = new DatabaseHandler(this);    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        recyclerView = (RecyclerView) findViewById(R.id.callRecyclerView);        mAdapter = new CallAdapter(contactList);        RecyclerView.LayoutManager mlayoutManager = new LinearLayoutManager(getApplicationContext());        recyclerView.setLayoutManager(mlayoutManager);        recyclerView.setItemAnimator(new DefaultItemAnimator());        recyclerView.setAdapter(mAdapter);        prepareCallData();    }    public void prepareCallData(){            contactList = db.getAllContacts();            for (Contact cn : contactList) {                Contact contact = new Contact(cn.getName(), cn.getPhoneNumber());                contactList.add(contact);                mAdapter.notifyDataSetChanged();            }        mAdapter.notifyDataSetChanged();        }    }