package com.example.greyscaler.phonerecord;import android.content.Context;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.DefaultItemAnimator;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import java.util.ArrayList;import java.util.List;public class MainActivity extends AppCompatActivity {    private List<Contact> contactList = new ArrayList<>();    private RecyclerView recyclerView;    Context mContext = MainActivity.this;    private CallAdapter mAdapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        DatabaseHandler db = new DatabaseHandler(mContext);        recyclerView = (RecyclerView) findViewById(R.id.callRecyclerView);        prepareCallData(db);        mAdapter = new CallAdapter(contactList);        RecyclerView.LayoutManager mlayoutManager = new LinearLayoutManager(getApplicationContext());        recyclerView.setLayoutManager(mlayoutManager);        recyclerView.setItemAnimator(new DefaultItemAnimator());        recyclerView.setAdapter(mAdapter);    }    public void prepareCallData(DatabaseHandler db){        db = new DatabaseHandler(mContext);        contactList = db.getAllContacts();            for (Contact cn : contactList) {                if(db.context != null) {                    Contact contact = new Contact(cn.getName(), cn.getPhoneNumber(), cn.getDate());                    contactList.add(contact);                    mAdapter.notifyDataSetChanged();                }else{                    return;                }            }    }    }